import "./AddProduct.css";
import React, { useState } from "react";

function AddProduct() {
  const [name, setName] = useState();
  const [description, setDescription] = useState();
  const [picture, setPicture] = useState();
  const [userId, setUserId] = useState();

  const handleInputChange = (e) => {
    const { id, value } = e.target;
    if (id === "name") {
      setName(value);
    }
    if (id === "description") {
      setDescription(value);
    }
    if (id === "picture") {
      setPicture(value);
    }
  };

  const handleSubmit = () => {
    if (name == "" && description == "" && !picture) {
      return;
    }

    let formData = {
      name: name,
      description: description,
      pictures: [picture],
      opened: true,
      userId: userId,
      offersId: [],
    };

    fetch("https://example.com/api/upload", {
      method: "POST",
      body: formData,
    })
      .then((response) => response.json())
      .then((data) => {
        // Handle the response data
        console.log(data);
      })
      .catch((error) => {
        // Handle any error that occurs during the request
        console.error(error);
      });
  };

  return (
    <div>
      <div className="header">
        <h3>Ajouter un produit</h3>
      </div>

      <div className="body">
        <div className="auth">
          <label for="auth">
            <p className="label-auth">Nom de l'objet</p>
          </label>
          <input
            type="text"
            id="name"
            value={name}
            onChange={(e) => handleInputChange(e)}
            placeholder="Veuilez saisir le nom de l'objet"
          />
        </div>
        <div className="auth">
          <label for="auth">
            <p className="label-auth">Description de l'objet</p>
          </label>
          <input
            type="text"
            value={description}
            onChange={(e) => handleInputChange(e)}
            id="description"
            placeholder="Veuilez saisir la description de l'objet"
          />
        </div>


        <div className="footer">
          <a href="http://localhost:3000/">
            <button className="btn btn-cancel">Annuler</button>
          </a>
          <div className="btn-right">
            <button className="btn btn-login" onClick={handleSubmit}>
              Ajouter le produit
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AddProduct;
